import React, { useState, useContext } from 'react'
import { TodoContext } from '../context'
import './Tasks.scss'
import Button from '../../UI/Button/Button'
import Task from '../Task/Task'
import { TaskServiсe, ListServiсe } from '../../API/Serviсe'


function Tasks() {
   const { lists, setLists, chosenList, setChosenList, currentPage } = useContext(TodoContext)
   const [newTask, setNewTask] = useState({
      value: '',
      done: false,
      listId: currentPage.toString()

   })



   const generateId = () => {
      let newId = 1
      lists.map(list => list.tasks.map(task =>
         newId < +task.id ? newId = task.id : ''
      ))
      console.log(++newId)
      return ++newId
   }


   const addTask = async (newTask) => {
      await TaskServiсe.postTask(newTask)
      lists.forEach(list => {
         if (+list.id === +newTask.listId) {
            list.tasks.push(newTask)
         }
      })
      console.log(lists)
      setLists(lists)

      // const updatedList = await ListServiсe.getLists()
      // setLists(updatedList)
      lists.forEach(list => list.id === chosenList.id ? setChosenList(list) : '')
      setNewTask({ value: '', listId: null })
   }

   const deleteTask = async (deletedTask) => {
      const newList =
         lists.map(list => {
            if (list.id === deletedTask.listId) {
               list.tasks = list.tasks.filter(task => task.id !== deletedTask.id)
            }
            return list
         })
      setLists(newList)
      await TaskServiсe.deleteTask(deletedTask)
   }

   return (
      <div className='tasks' >
         <h1 className='tasks__title' >
            {chosenList.title}
         </h1>

         <input
            value={newTask.value}
            onChange={e => setNewTask({ value: e.target.value, listId: currentPage.toString(), id: generateId().toString() })}
            className='tasks__input input'
            type="text"
            placeholder={`Create task on '${chosenList.title}' category`} />
         {newTask.value &&
            <Button
               onClick={() => addTask(newTask)}
               title={'Create new task'}
               className={'new-task-btn'}
            />
         }
         <hr className='tasks__line' />

         {currentPage !== 1 ?
            chosenList.tasks.map(task =>
               <Task
                  key={task.id}
                  task={task}
                  deleteTask={() => deleteTask(task)}
               />)
            :
            lists.map(list => list.tasks.map(task =>
               <Task
                  key={task.id}
                  task={task}
                  title={list.title}
                  list={list}
                  deleteTask={() => deleteTask(task)}
               />))
         }
      </div>
   )
}

export default Tasks
