import React, { useState, useContext } from 'react'
import { TodoContext } from '../context'
import './Tasks.scss'
import Button from '../../UI/Button/Button'
import Task from '../Task/Task'
import PostService from '../../API/PostServise'



function Tasks() {
   const { lists, setLists, chosenList,setChosen } = useContext(TodoContext)

   const [newTask, setNewTask] = useState({ value: '', listId: chosenList.id, })

   const addTask = (newTask) => {
      if (newTask.value) {
         const newList = lists.map(item => {
            if (+item.id === +newTask.listId) {
               item.tasks = [...item.tasks, newTask]
            }
            return item
         })
         setLists(newList)
      }
      PostService.postTask(newTask)
      setNewTask({ value: '', listId: null })
   }

   const deleteTask = (deletedTask) => {
      if (chosenList) {
         const newChosenList = chosenList.tasks.filter(task => +task.id !== +deletedTask.id)
         setChosenList(newChosenList)
      } else {
         const newList = lists.map(item => {
            if (+item.id === +deletedTask.listId) {
               item.tasks = item.tasks.filter(task => +task.id !== +deletedTask.id)
            }
            return item
         })
         setLists(newList)
      }



      // PostService.deleteTask(deletedTask)
   }



   const categoryTitle = () => {
      if (chosenList.id) {
         return chosenList.title
      } else {
         return 'All'
      }
   }



   const renderTasks = () => {
      if (chosenList) {
         return chosenList.tasks.map(task =>
            <Task
               key={task.id}
               task={task}
            />)
      } else {
         return lists.map(list => list.tasks.map(task =>
            < Task
               key={task.id}
               task={task}
            />))
      }
   }

   return (
      <div className='tasks' >
         <h1 className='tasks__title' >
            {categoryTitle()}
         </h1>

         <input
            value={newTask.value}
            onChange={e => setNewTask({ value: e.target.value, id: Date.now(), listId: chosenList.id })}
            className='tasks__input input'
            type="text"
            placeholder={`Create task on '${categoryTitle()}' category`} />
         <Button
            onClick={() => addTask(newTask)}
            title={'Create new task'}
            className={'new-task-btn'}
         />
         <hr className='tasks__line' />

         {renderTasks()}
      </div>
   )
}

export default Tasks
