import React from 'react'
import './Task.scss'
import { BiTrashAlt } from "react-icons/bi"
import Checkbox from '../../UI/Checkbox/Checkbox'
// import PostService from '../../API/PostServise'

function Task({ task, chosenList, lists }) {

   const getCategory = () => {
      const taskCategory = lists.find(list => list.id === task.listId)
      return taskCategory.title
   }

   const deleteTask = (deletedTask) => {

      // console.log(lists.filter(list => list.tasks.filter(task => +task.id === deletedTask.id)))

      // PostService.deleteTask(deletedTask)
   }

   return (
      <div className="task">
         <Checkbox />
         <p className='task__value' >{task.value}</p>
         {chosenList.id ?
            ''
            :
            <div className="task__category">{getCategory()}</div>
         }

         <BiTrashAlt
            className='task__delete-icon'
            onClick={() => deleteTask(task)}
         />
      </div>
   )
}

export default Task
