import React, { useState } from 'react'
import './Tasks.scss'
import Button from '../../UI/Button/Button'
import Task from '../Task/Task'


function Tasks({ lists }) {

   const [newTask, setNewTask] = useState()

   const addTask = (newTaskValue) => {
      setNewTask(newTaskValue)
      setNewTask('')
   }


   return (
      <div className='tasks' >
         <h1 className='tasks__title' >All Tasks</h1>
         <Button
            title={'Create new task'}
            className={'new-task-btn'}
         />
         <input
            value={newTask}
            onChange={e => addTask(e.target.value)}
            className='tasks__input input'
            type="text"
            placeholder="Create task on 'All' category" />

         <hr className='tasks__line' />

         <Task />
      </div>
   )
}

export default Tasks
