import React, { useState } from 'react'
import './Categories.scss'
import PostService from '../../API/PostServise'
import { BiX } from "react-icons/bi";

function Categories({ lists, setLists }) {

  const [newList, setNewList] = useState('')

  const addList = () => {
    setLists([...lists, { title: newList, id: '' }])
    PostService.postList({ title: newList, id: '' })
    setNewList('')
  }

  const deleteList = (deletedList) => {
    Post
    setLists(lists.filter(list => list.id !== deletedList.id))
    PostService.deleteList(deletedList)
  }

  return (
    <div className='categories' >
      <img src='img/logo.png' alt='logo' />
      <ul className='categories__list'>
        <li className='categories__item' >All </li>
        {lists.map(list =>
          <li key={list.id} className='categories__item' >
            {list.title}
            <BiX
              onClick={() => deleteList(list)}
              className='delete-item'
            />
          </li>
        )}
      </ul>
      <input
        value={newList}
        onChange={e => setNewList(e.target.value)}
        className='categories__input'
        type="text"
        placeholder='New category' />
      <button onClick={addList} className='categories__button button' >Add</button>
    </div>
  )
}

export default Categories
