import React, { useState } from 'react'
import './Tasks.scss'
import Button from '../../UI/Button/Button'
import Task from '../Task/Task'
import PostService from '../../API/PostServise'



function Tasks({ lists, chosenList }) {
   const [newTask, setNewTask] = useState('')
   // const [allTasks, setAllTasks] = useState([])

   // useEffect(() => {
   //    const getAllTaks = () => {
   //       lists.forEach(list =>
   //          list.tasks.forEach(task =>
   //             setAllTasks([...allTasks, task])))
   //    }
   //    getAllTaks()
   // }, [])
   // console.log(allTasks)

   const addTask = (newTask) => {
      // setLists(...lists[chosenList.id - 1].tasks, {
      //    listId: chosenList.id,
      //    value: newTask,
      // })
      console.log([...lists[chosenList.id - 1].tasks, {
         id: '',
         listId: chosenList.id,

      }])
      PostService.postTask({
         listId: chosenList.id,
         value: newTask,
      })
      setNewTask('')
   }

   const categoryTitle = () => {
      if (chosenList.id) {
         return chosenList.title
      } else {
         return 'All'
      }
   }



   const renderTasks = () => {
      if (chosenList) {
         return (
            lists.map(list => list.tasks.map(task =>
               task.listId === chosenList.id ?
                  <Task
                     key={task.id}
                     task={task}
                     chosenList={chosenList}
                  /> : ''
            ))
         )
      } else {
         return (
            lists.map(list => list.tasks.map(task =>
               < Task
                  key={task.id}
                  task={task}
                  chosenList={chosenList}
                  lists={lists}
               />
            ))
         )
      }
   }



   return (
      <div className='tasks' >
         <h1 className='tasks__title' >
            {categoryTitle()}
         </h1>

         <input
            value={newTask}
            onChange={e => setNewTask(e.target.value)}
            className='tasks__input input'
            type="text"
            placeholder={`Create task on '${categoryTitle()}' category`} />
         <Button
            onClick={() => addTask(newTask)}
            title={'Create new task'}
            className={'new-task-btn'}
         />
         <hr className='tasks__line' />

         {renderTasks()}
      </div>
   )
}

export default Tasks
