import React, { useContext } from 'react'
import { TodoContext } from '../context'
import './Task.scss'
import { BiTrashAlt } from "react-icons/bi"
import Checkbox from '../../UI/Checkbox/Checkbox'
// import PostService from '../../API/PostServise'

function Task({ task }) {
   const { lists, setLists, chosenList, setChosenList } = useContext(TodoContext)

   const getCategory = () => {
      const taskCategory = lists.find(list => list.id === task.listId)
      return taskCategory.title
   }

   const deleteTask = (deletedTask) => {
      if (chosenList) {
         const newChosenList = chosenList.tasks.filter(task => +task.id !== +deletedTask.id)
         setChosenList(newChosenList)
      } else {
         const newList = lists.map(item => {
            if (+item.id === +deletedTask.listId) {
               item.tasks = item.tasks.filter(task => +task.id !== +deletedTask.id)
            }
            return item
         })
         setLists(newList)
      }



      // PostService.deleteTask(deletedTask)
   }

   return (
      <div className="task">
         <Checkbox />
         <p className='task__value' >{task.value}</p>
         {chosenList.id ?
            ''
            :
            <div className="task__category">{getCategory()}</div>
         }

         <BiTrashAlt
            className='task__delete-icon'
            onClick={() => deleteTask(task)}
         />
      </div>
   )
}

export default Task
