import React, { useContext } from 'react'
import './Task.scss'
import { BiTrashAlt } from "react-icons/bi"
import Checkbox from '../../UI/Checkbox/Checkbox'
import { TodoContext } from '../context'

function Task({ task, deleteTask, list }) {
   const { setChosenList, navigate } = useContext(TodoContext)

   const changePage = () => {
      setChosenList(list)
      navigate(`lists/${list.id}`)
   }

   return (
      <div className="task">
         <Checkbox id={task.id} />
         <p className='task__value' >{task.value}</p>
         {list.title ? <div  className="task__category">{list.title}</div> : ''}
         <BiTrashAlt
            className='task__delete-icon'
            onClick={() => deleteTask(task)}
         />
      </div>
   )
}

export default Task
