import React, { useState } from 'react'
import './Categories.scss'
import PostService from '../../API/PostServise'
import List from '../List/List'
import Button from '../../UI/Button/Button'

function Categories({ lists, setLists, chosenList, setChosenList }) {
  const [newList, setNewList] = useState('')

  const addList = () => {
    if (newList.length) {
      setLists([...lists, { title: newList, id: (lists.length + 1), tasks: [] }])
      PostService.postList({ title: newList, id: (lists.length + 1), tasks: [] })
    }
    setNewList('')
  }



  const deleteList = async (deletedList) => {
    setLists(lists.filter(list => list.id !== deletedList.id))
    // deletedList.tasks.map(async task =>
    //   await new Promise((reslove, reject) =>
    //     reslove(PostService.deleteTask(task))
    //   )
    // )


    await PostService.deleteList(deletedList)
    setChosenList(0)
  }

  return (
    <div className='categories' >
      <img src='img/logo.png' alt='logo' />
      <ul className='categories__list'>
        <li
          className={chosenList.id ? 'categories__item' : 'categories__item chosen'}
          onClick={() => setChosenList(0)}>All</li>
        {lists.map(list =>
          <List
            key={list.id}
            list={list}
            deleteList={deleteList}
            chosenList={chosenList}
            setChosenList={setChosenList}
          />
        )}
      </ul>
      <input
        value={newList}
        onChange={e => e.target.value.length < 16 ? setNewList(e.target.value) : alert('max categories length = 16')}
        className='categories__input input'
        type="text"
        placeholder='New category' />
      <Button
        onClick={addList}
        title={'Add'}
        className={'categories__button'}
      />
    </div>
  )
}

export default Categories
