import React, { useState, useContext, useEffect } from 'react'
import { TodoContext } from '../context'
import './Tasks.scss'
import Button from '../../UI/Button/Button'
import Task from '../Task/Task'
import PostService from '../../API/PostServise'



function Tasks({ tasks }) {
   const { lists, setLists, chosenList } = useContext(TodoContext)
   const [currentTasks, setCurrentTasks] = useState([])
   const [newTask, setNewTask] = useState({ value: '', listId: chosenList.id, })

   useEffect(() => {
      setCurrentTasks(tasks)
   }, [tasks])


   const addTask = (newTask) => {
      // if (newTask.value) {
      //    const newList = lists.map(item => {
      //       if (+item.id === +newTask.listId) {
      //          item.tasks = [...item.tasks, newTask]
      //       }
      //       return item
      //    })
      //    setLists(newList)
      // }
      if (newTask.value) {
         setCurrentTasks([...currentTasks, newTask])

      }
      console.log(lists[chosenList.id])

      PostService.postTask(newTask)
      setNewTask({ value: '', listId: null })
   }

   const deleteTask = (deletedTask) => {
      setCurrentTasks(currentTasks.filter(task => task.id !== deletedTask.id))
      PostService.deleteTask(deletedTask)
   }

   const categoryTitle = () => {
      return chosenList.id ? chosenList.title : 'All'
   }

   return (
      <div className='tasks' >
         <h1 className='tasks__title' >
            {categoryTitle()}
         </h1>

         <input
            value={newTask.value}
            onChange={e => setNewTask({ value: e.target.value, id: Date.now(), listId: chosenList.id })}
            className='tasks__input input'
            type="text"
            placeholder={`Create task on '${categoryTitle()}' category`} />
         <Button
            onClick={() => addTask(newTask)}
            title={'Create new task'}
            className={'new-task-btn'}
         />
         <hr className='tasks__line' />

         {currentTasks.map(task =>
            <Task
               key={task.id}
               task={task}
               deleteTask={() => deleteTask(task)}
            />)}
      </div>
   )
}

export default Tasks
