import React, { useState, useEffect } from "react";
import Categories from "./Components/Categories/Categories";
import { ListServiсe, TaskServiсe } from "./API/Serviсe";
import Tasks from "./Components/Tasks/Tasks";
import { TodoContext } from './Components/context'
import { Route, Routes, useLocation, useNavigate } from 'react-router-dom'

function App() {
  const [lists, setLists] = useState([])
  const [allTasks, setAllTasks] = useState([])
  const [chosenList, setChosenList] = useState(0)

  let location = useLocation()
  const currentPage = location.pathname.split('/').slice(-1)
  let navigate = useNavigate()

  useEffect(() => {
    async function fetchData() {
      const listResponse = await ListServiсe.getLists()
      setLists(listResponse)

      const tasksResponse = await TaskServiсe.getAllTasks()
      setAllTasks(tasksResponse)
    }
    fetchData()
  }, [], [lists, location])



  return (
    <TodoContext.Provider value={{
      lists,
      setLists,
      chosenList,
      setChosenList,
      currentPage,
      navigate,
      setAllTasks
    }} >

      <div className="app">

        <Categories />

        <Routes>
          <Route path='/' element={<Tasks tasks={allTasks} />} />

          {lists.map(list =>
            <Route
              key={list.id}
              path={`lists/${list.id}`}
              element={<Tasks tasks={list.tasks} />} />)
          }
        </Routes>
      </div>
    </TodoContext.Provider>
  );
}

export default App;
