import React, { useState } from 'react'
import './Tasks.scss'
import Button from '../../UI/Button/Button'
import Task from '../Task/Task'
import PostService from '../../API/PostServise'



function Tasks({ lists, allTasks, setAllTasks, chosenList }) {
   const [newTask, setNewTask] = useState()

   const categoryTitle = () => {
      if (chosenList.id) {
         return chosenList.title
      } else {
         return 'All'
      }
   }



   const addTask = () => {

      setAllTasks([...allTasks, {
         listId: chosenList.id,
         value: newTask,
      }])


      PostService.postTask({
         listId: chosenList.id,
         value: newTask,
      })
   }

   return (
      <div className='tasks' >
         <h1 className='tasks__title' >
            {categoryTitle()}
         </h1>

         <input
            value={newTask}
            onChange={e => setNewTask(e.target.value)}
            className='tasks__input input'
            type="text"
            placeholder={`Create task on '${categoryTitle()}' category`} />
         <Button
            onClick={addTask}
            title={'Create new task'}
            className={'new-task-btn'}
         />
         <hr className='tasks__line' />
         {chosenList.id ?
            lists[chosenList.id].tasks.map(task =>
               <Task
                  key={task.id}
                  value={task.value}
                  chosenList={chosenList}
               />)
            :
            lists.map(list => list.tasks.map(task =>
               <Task
                  key={task.id}
                  value={task.value}
                  chosenList={chosenList}
               />
            )

         )}
      </div>
   )
}

export default Tasks
