import React, { useContext } from 'react'
import { TodoContext } from '../context'
import { BiX } from "react-icons/bi"
import './List.scss'

function List({ list, deleteList }) {
   const { chosenList, setChosenList } = useContext(TodoContext)


   return (
      <li
         onClick={() => setChosenList(list.id === 0 ? 0 : list)}
         className={chosenList.id === list.id || chosenList === 0 ? 'categories__item chosen' : 'categories__item'} >
         {list.title}
         {deleteList &&
            <BiX
               onClick={() => deleteList(list)}
               className='delete-item'
            />
         }

      </li>
   )
}

export default List
