import React from 'react'
import './Task.scss'
import { BiTrashAlt } from "react-icons/bi"
import Checkbox from '../../UI/Checkbox/Checkbox'

function Task({ task, chosenList, lists }) {

   const getCategory = () => {
      for (list in lists) {
         if (list.id === task.listId)
            return list.title
      }
   }



   return (
      <div className="task">
         <Checkbox />
         <p className='task__value' >{task.value}</p>
         {chosenList.id ?
            ''
            :
            <div className="task__category">{ }</div>
         }

         <BiTrashAlt className='task__delete-icon' />
      </div>
   )
}

export default Task
