import React, { useState, useContext } from 'react'
import './Categories.scss'
import { TodoContext } from '../context'
import { ListServiсe, TaskServiсe } from '../../API/Serviсe'
import List from '../List/List'
import Button from '../../UI/Button/Button'
import { Link } from 'react-router-dom'

function Categories() {
  const { lists, setLists, chosenList, setChosenList } = useContext(TodoContext)
  const [newList, setNewList] = useState('')


  const addList = async () => {
    if (newList.length) {
      // setLists([...lists, { title: newList, id: '', tasks: [] }])
      ListServiсe.postList({ title: newList, id: '', tasks: [] })
      const list = await ListServiсe.getLists()
      setLists(list)
      renderLists()
    }
    setNewList('')
  }

  const deleteList = async (deletedList) => {
    setLists(lists.filter(list => list.id !== deletedList.id))
    deletedList.tasks.map(task =>
      new Promise(reslove =>
        reslove(TaskServiсe.deleteTask(task))
      )
    )
    await ListServiсe.deleteList(deletedList)
    setChosenList(0)
  }

  const renderLists = () => {
    return lists.map(list =>
      <List
        key={list.id}
        list={list}
        deleteList={deleteList}
        chosenList={chosenList}
        setChosenList={setChosenList}
      />
    )
  }

  return (
    <div className='categories' >
      <img src='/img/logo.svg' alt='logo' />
      <ul className='categories__list'>
        <Link to='/' style={{ textDecoration: 'none' }} >
          <li
            className={chosenList.id ? 'categories__item' : 'categories__item chosen'}
            onClick={() => setChosenList(0)}>
            All
          </li>
        </Link>

        {/* {lists.map(list =>
          <List
            key={list.id}
            list={list}
            deleteList={deleteList}
            chosenList={chosenList}
            setChosenList={setChosenList}
          />
        )} */}
        {renderLists()}
      </ul>
      <input
        value={newList}
        onChange={e => e.target.value.length < 16 ? setNewList(e.target.value) : alert('max categories length = 16')}
        className='categories__input input'
        type="text"
        placeholder='New category' />
      <Button
        onClick={addList}
        title={'Add'}
        className={'categories__button'}
      />
    </div>
  )
}

export default Categories
