import React, { useState, useContext } from 'react'
import { TodoContext } from '../context'
import './Tasks.scss'
import Button from '../../UI/Button/Button'
import Task from '../Task/Task'
import { TaskServiсe, ListServiсe } from '../../API/Serviсe'



function Tasks() {
   const { chosenList, setLists, currentPage } = useContext(TodoContext)
   const [newTask, setNewTask] = useState({ value: '', listId: +currentPage })

   const addTask = async (newTask) => {
      if (newTask.value) {
         await TaskServiсe.postTask(newTask)
         const updatedList = await ListServiсe.getLists()
         setLists(updatedList)
      }
      console.log(newTask)
      setNewTask({ value: '', listId: null })
   }


   const deleteTask = (deletedTask) => {
      tasks = tasks.filter(task => task.id !== deletedTask.id)
      TaskServiсe.deleteTask(deletedTask)
   }

   const categoryTitle = () => {
      return +currentPage ? chosenList.title : 'All'
   }

   return (
      <div className='tasks' >
         <h1 className='tasks__title' >
            {categoryTitle()}
         </h1>

         <input
            value={newTask.value}
            onChange={e => setNewTask({ value: e.target.value, listId: +currentPage })}
            className='tasks__input input'
            type="text"
            placeholder={`Create task on '${categoryTitle()}' category`} />
         <Button
            onClick={() => addTask(newTask)}
            title={'Create new task'}
            className={'new-task-btn'}
         />
         <hr className='tasks__line' />

         {tasks.map(task =>
            <Task
               key={task.id}
               task={task}
               deleteTask={() => deleteTask(task)}
            />)}
      </div>
   )
}

export default Tasks
