import React, { useState } from 'react'
import './Tasks.scss'
import Button from '../../UI/Button/Button'
import Task from '../Task/Task'
import PostService from '../../API/PostServise'



function Tasks({ lists, setLists, chosenList }) {
   const [newTask, setNewTask] = useState({ value: '', listId: '' })


   const addTask = (newTask) => {

      // console.log(newTask)

      const newList = lists.map(item => {
         console.log(item.id === newTask.listId)
         // if (item.id === newTask.listId) {
         //    item.tasks = [...item.task, newTask]
         // }
         // return newList
      })
      // console.log(newList)







      // PostService.postTask(newTask)
      setNewTask('')
   }

   const categoryTitle = () => {
      if (chosenList.id) {
         return chosenList.title
      } else {
         return 'All'
      }
   }



   const renderTasks = () => {
      if (chosenList) {
         return chosenList.tasks.map(task =>
            <Task
               key={task.id}
               task={task}
               chosenList={chosenList}
            />)
      } else {
         return lists.map(list => list.tasks.map(task =>
            < Task
               key={task.id}
               task={task}
               chosenList={chosenList}
               lists={lists}
            />))
      }
   }

   return (
      <div className='tasks' >
         <h1 className='tasks__title' >
            {categoryTitle()}
         </h1>

         <input
            value={newTask.value}
            onChange={e => setNewTask({ value: e.target.value, listId: chosenList.id })}
            className='tasks__input input'
            type="text"
            placeholder={`Create task on '${categoryTitle()}' category`} />
         <Button
            onClick={() => addTask(newTask)}
            title={'Create new task'}
            className={'new-task-btn'}
         />
         <hr className='tasks__line' />

         {renderTasks()}
      </div>
   )
}

export default Tasks
